import React from "react";
import Card from "./components/Card";
import Header from "./components/Header";
import Drawer from "./components/Drawer"

const arr  = [
    {title: 'Мужские Кроссовки Nike Blazer Mid Suede', price: 12999, imageUrl: ' /img/sneakers/1.jpg'},
    {title: 'Мужские Кроссовки Nike Air Max 270',  price: 12999, imageUrl: ' /img/sneakers/2.jpg' },
    {title: 'Мужские Кроссовки Nike Blazer Mid Suede',  price: 8499, imageUrl: ' /img/sneakers/3.jpg'},
    {title: 'Кроссовки Puma X Aka Boku Future Rider',  price: 8999, imageUrl: ' /img/sneakers/4.jpg'},
]

function App() {
        let count = 5;
        const test = React.useState(count)


        function plus(){
            count++
        }
        function minus(){
            count--
        }

  return (
    <div className="wrapper clear">
        <center className="pt-40">
            <h1>{test}</h1>
            <button onClick={plus}>+</button>
            <button onClick={minus}>-</button>
        </center>
        <Drawer />
        <Header />
      <section className="content p-40">
          <div className="d-flex justify-between align-center mb-40">
              <h1>Все кроссовки</h1>
              <div className="search-block">
                  <img src="/img/search.svg" alt="search"/>
                  <input placeholder="Поиск..." type="text"/>
              </div>
          </div>
          <div className="d-flex">
              {arr.map((obj) =>(<Card
                  title={obj.title}
                  price={obj.price}
                  imageUrl={obj.imageUrl}
                  onPlus={()=>{console.log('Добавили в карзину')}}
                  onFavorite={()=>{console.log('Добавили в закладки')}}
              /> ))}
          </div>
      </section>
    </div>
  );
}
export default App;
