import React from "react";
import styles from './Card.module.scss'

function Card(isadd) {
    const [isAdded, setIsAdded] = React.useState(false)
    const [isFollow, setIsFollow] = React.useState(false)
    const onClickPlus = () =>{
        setIsAdded(!isAdded)
    }
    const onClickFollow = () => {
        setIsFollow(!isFollow)
    }
    return (
        <div className={styles.card}  >
            <div className={styles.favorite} onClick={onClickFollow}>
                <img src={isFollow ? "/img/heartLiked.svg" :  "/img/heartUnLiked.svg "} alt="Unliked"/>
            </div>
            <img width={133} height={112} src={props.imageUrl} alt="Sneakers"/>
            <h5>{props.title}</h5>
            <div className="d-flex justify-between align-center">
                <div className="d-flex flex-column">
                      <span>
                          Цена:
                      </span>
                    <b>{props.price} руб.</b>
                </div>
                    <img onClick={onClickPlus} className={styles.btn} src={isAdded ? '/img/btn-cheaked.svg ' : '/img/btnPlus.svg'} alt="plus"/>
            </div>
        </div>
    )
}
export default Card
