import React from "react";
import axios from "axios";
import {Route} from "react-router-dom";
import Header from "./components/Header";
import Drawer from "./components/Drawer"
import Favorite from "./components/pages/Favorite/index"
import Slider from './components/Slider/index'
import Home from "./components/pages/Home";
import favorite from "./components/pages/Favorite/index";
function App() {

    const [items, setItems] = React.useState([])
    const [cartItems, setCartItems] = React.useState([])
    const [searchValue, setSearchValue] = React.useState("")
    const [cartOpened, setCartOpened] = React.useState(false)
    const [isFavorite, setIsFavorite] = React.useState(false)
    const [isReady, setIsReady] = React.useState(false)
    React.useEffect(() => {
        axios.get('https://64a01998ed3c41bdd7a6ff00.mockapi.io/Cart').then(res => {
            setCartItems(res.data)
        }).then(() => {
            axios.get('https://64a412e5c3b509573b570399.mockapi.io/follow').then(res => {
                setIsFavorite(res.data)
            }).then(() => {
                axios.get('https://64a01998ed3c41bdd7a6ff00.mockapi.io/items').then(res => {
                setItems(res.data)
                }).then(() => {
                    setIsReady(true)
                })
            })
        })
    }, [])

    const onRemoveToCart = (id) => {
        axios.delete(`https://64a01998ed3c41bdd7a6ff00.mockapi.io/Cart/${id}`)
        setCartItems((prev) => prev.filter((item) => item.id !== id))
    }
    const onAddToCart = (obj)=>{
        try{
            if (cartItems.find(item => Number(item.id) === Number(obj.id))){
                setCartItems(prev => prev.filter(item => Number(item.id) !== Number(obj.id)))
                axios.delete(`https://64a01998ed3c41bdd7a6ff00.mockapi.io/Cart/${obj.id}`)
            }
            else{
                axios.post('https://64a01998ed3c41bdd7a6ff00.mockapi.io/Cart', obj);
                setCartItems(prev => [...prev, obj]);
            }
        }
        catch (error){
            alert(error)
        }
    }

    const onChangeSearchInput = (event) =>{
        setSearchValue(event.target.value)
    }
    const openCart = () => {
        axios.get('https://64a01998ed3c41bdd7a6ff00.mockapi.io/Cart').then(res => {
            setCartItems(res.data)
        })
        setCartOpened(true)
    }
    const onClickFavorite = (obj) => {
        if (isFavorite.find (item => item.id === obj.id)){
            axios.delete(`https://64a412e5c3b509573b570399.mockapi.io/follow/${obj.id}`)
        }
        else{
            axios.post('https://64a412e5c3b509573b570399.mockapi.io/follow', obj).then((data) => {
                setIsFavorite((prev) => [...prev, data]);
            })
        }
    }
    const priceCounter = () => {
        let priceCount = []
        cartItems.map(obj => priceCount.push(obj.price))
        return (priceCount.length > 0 ? priceCount.reduce((a,b) => a+b) : 0)
    }
  return (
    <div className="wrapper clear">
        {cartOpened ? <Drawer items={cartItems} Count = {priceCounter()} onClickClose={() => setCartOpened(false) } onRemove={onRemoveToCart}/> : null }
        <Header key={"Header"} onClickCart={openCart} Count = {priceCounter()}/>
        <Route path="/" exact>
            <Slider />

            <Home
                items={items}
                cartItems={cartItems}
                searchValue={searchValue}
                onAddToCart={onAddToCart}
                onClickFavorite={onClickFavorite}
                onChangeSearchInput={onChangeSearchInput}
                loading={isReady}
            />
        </Route>
        <Route path="/favorite" exact>
            <Favorite
                items={isFavorite}
                onClickFavorite={onClickFavorite}/>
        </Route>

    </div>
  );
}
export default App;
