function Drawer ({onRemove,onClickClose, items = []}){
    let priceCount = []
    items.map(obj => priceCount.push(obj.price))

    const priceCounter = () =>{}
    return(
    <div className="overlay">
        <aside className="drawer d-flex flex-column">
            <h3 className="mb-30 d-flex justify-between">Корзина
                <img onClick={onClickClose} className="remove-btn" src="/img/btn-premove.svg" alt="remove"/>
            </h3>
            {/*<div className="items flex">*/}
            {/*//     {items.map((obj) =>(*/}
            {/*//         <div className="cart-list mb-20 d-flex align-center ">*/}
            {/*//             <div className="cart-item-img" style={{backgroundImage: `url(${obj.imageUrl})`}}></div>*/}
            {/*//             <div className="mr-20 flex">*/}
            {/*                <p className="mb-5">{obj.title}</p>*/}
            {/*                <b>{obj.price} руб.</b>*/}
            {/*            </div>*/}
            {/*            <img onClick={() => onRemove(obj.id)} className="remove-btn" src="/img/btn-premove.svg" alt="remove"/>*/}
            {/*        </div>*/}
            {/*    ))}*/}
            {/*</div>*/}
            <div className={"emptyCart flex"}>
                
            </div>
            <div className="cart-price">
                <ul className="cart-total-block mb-30">
                    <li>
                        <span>Итого:</span>
                        <div></div>
                        <b>{priceCount.length > 0 ? priceCount.reduce((a,b) => a+b) : 0} руб.</b>
                    </li>
                    <li>
                        <span>Налог 5%:</span>
                        <div></div>
                        <b>{priceCount.length > 0 ? priceCount.reduce((a,b) => a+b)*5/100 : 0} руб.</b>
                    </li>
                </ul>
                <button className="green-btn">Оформить заказ
                    <img src="/img/arrow.svg" alt="arrow"/>
                </button>
            </div>
        </aside>
    </div>
    )
}

export default Drawer
