import React from "react";
import styles from './Favorite.module.scss'
import Card from "./../../Card/index";
import {Link} from "react-router-dom";
import AppContext from "../../../Context";


const Favorite = ({onClickFavorite}) => {
    const {isFavorite} = React.useContext(AppContext)
    const [FavoriteList, setFavoriteList] = React.useState([])

    return (
        isFavorite.length > 0 ?
            <section className={styles.content}>
                <h1>Мои закладки</h1>
                <div className="d-flex flex-wrap">
                    {isFavorite.map(obj => (
                        <Card
                            favorite={true}
                            onFavorite={(obj) => onClickFavorite(obj)}
                            {...obj}
                        />
                    ))}
                </div>
            </section> :
            <section className={styles.emptyContent}>
                <div className={styles.favoriteContent}>
                    <img height={70} width={70} src="/img/sad-smile.png" alt="smile"/>
                    <div className={styles.favoriteDescription}>
                        <h2>Закладок нет :(</h2>
                        <p>Вы ничего не добавили в закладки</p>
                    </div>
                    <Link to="/">
                        <button className={styles.favoriteBtn}>
                            <img src="/img/array-left.png" alt="array"/>
                            Вернутся назад
                        </button>
                    </Link>
                </div>
            </section>
    )}

export default Favorite
