import React from "react";
import styles from './Card.module.scss'
import ContentLoader from "react-content-loader"

function Card({onFavorite, title,price, imageUrl, onPlus, favorite= false, id, added = false}) {
    const [isAdded, setIsAdded] = React.useState(added)
    const [isFollow, setIsFollow] = React.useState(favorite)
    const onClickPlus = () =>{
        onPlus({id, title, price, imageUrl})
        setIsAdded(!isAdded)
    }
    const onClickFollow = () => {
        onFavorite({id, title, price, imageUrl})
        setIsFollow(!isFollow)
    }
    return (
        <ContentLoader
            speed={2}
            width={200}
            height={210}
            viewBox="0 0 200 210"
            backgroundColor="#e3e3e3"
            foregroundColor="#fafafa"
        >
            <rect x="0" y="0" rx="10" ry="10" width="150" height="90" />
            <rect x="0" y="105" rx="5" ry="5" width="150" height="15" />
            <rect x="NaN" y="NaN" rx="0" ry="0" width="NaN" height="NaN" />
            <rect x="0" y="125" rx="5" ry="5" width="94" height="15" />
            <rect x="0" y="154" rx="10" ry="10" width="80" height="25" />
            <rect x="118" y="148" rx="5" ry="5" width="32" height="32" />
        </ContentLoader>
    )
}
export default Card
