import React from "react";
import styles from './Card.module.scss'
import ContentLoader from "react-content-loader"
import AppContext from "./../../Context";

function Card({onFavorite, title,price, imageUrl, onPlus, favorite= false, id, added= false}) {
    const {isItemAdded} = React.useContext(AppContext)
    const [isAdded, setIsAdded] = React.useState(isItemAdded)
    const [isFollow, setIsFollow] = React.useState(favorite)
    const onClickPlus = () =>{
        onPlus({id, title, price, imageUrl})
        setIsAdded(!isAdded)
    }
    const onClickFollow = () => {
        onFavorite({id, title, price, imageUrl})
        setIsFollow(!isFollow)
    }
    return (
        <div className={styles.card}>
                <div className={styles.favorite} onClick={onClickFollow}>
                        <img src={isFollow ? "/img/heartLiked.svg" :  "/img/heartUnLiked.svg "} alt="Unliked"/>
                    </div>
                    <img width={133} height={112} src={imageUrl} alt="Sneakers"/>
                    <h5>{title}</h5>
                    <div className="d-flex justify-between align-center">
                        <div className="d-flex flex-column">
                          <span>
                              Цена:
                          </span>
                            <b>{price} руб.</b>
                        </div>
                        <img onClick={onClickPlus} className={styles.btn} src={isAdded ? '/img/btn-cheaked.svg ' : '/img/btnPlus.svg'} alt="plus"/>
                </div>
        </div>


    )
}
export default Card
