import React, {useContext} from "react";
import styles from './Favorite.module.scss'
import Card from "./../../Card/index";
import {Link} from "react-router-dom";
import AppContext from "./../../../Context";
import Info from "./../../Info/index";



const Favorite = ({items, onClickFavorite}) => {
    const {onAddToCart} = useContext(AppContext)
    return (
        items.length > 0 ?
            <section className={styles.content}>
                <h1>Мои закладки</h1>
                <div className="d-flex flex-wrap">
                    {items.map((obj, index) => (
                        <Card
                            key={index}
                            favorite={true}
                            onPlus={(obj) => onAddToCart(obj)}
                            onFavorite={(obj) => onClickFavorite(obj)}
                            {...obj}
                        />
                    ))}
                </div>
            </section> :
            <section className={styles.emptyContent}>
                <div className={styles.favoriteContent}>
                    {/*<img height={70} width={70} src="/img/sad-smile.png" alt="smile"/>*/}
                    {/*<div className={styles.favoriteDescription}>*/}
                    {/*    <h2>Закладок нет :(</h2>*/}
                    {/*    <p>Вы ничего не добавили в закладки</p>*/}
                    {/*</div>*/}
                    {/*<Link to="/">*/}
                    {/*    <button className={styles.favoriteBtn}>*/}
                    {/*        <img src="/img/array-left.png" alt="array"/>*/}
                    {/*        Вернутся назад*/}
                    {/*    </button>*/}
                    {/*</Link>*/}
                    <Info
                        title={'У вас нет заказов'}
                        description={'Оформите хотя бы один заказ.'}
                        imageUrl={'/img/sad-smile-order.png'}
                        backHome={true}
                    />
                </div>
            </section>
    )}

export default Favorite
