import React from "react";
import Card from "./components/Card";
import Header from "./components/Header";
import Drawer from "./components/Drawer"

function App() {
    const [items, setItems] = React.useState([])
    const [cartOpened, setCartOpened] = React.useState(false)

    fetch('https://64a01998ed3c41bdd7a6ff00.mockapi.io/items').then((respone) =>{
        return respone.json()}).then(json =>{setItems(json)})

  return (
    <div className="wrapper clear">
        {cartOpened ? <Drawer onClickClose={()=> setCartOpened(false)}/> : null }
        <Header onClickCart={ () => setCartOpened(true)}/>
      <section className="content p-40">
          <div className="d-flex justify-between align-center mb-40">
              <h1>Все кроссовки</h1>
              <div className="search-block">
                  <img src="/img/search.svg" alt="search"/>
                  <input placeholder="Поиск..." type="text"/>
              </div>
          </div>
          <div className="d-flex flex-wrap">
              {items.map((obj) =>(<Card
                  title={obj.title}
                  price={obj.price}
                  imageUrl={obj.imageUrl}
                  onPlus={()=>{console.log('Добавили в карзину')}}
                  onFavorite={()=>{console.log('Добавили в закладки')}}
              /> ))}
          </div>
      </section>
    </div>
  );
}
export default App;
