import React from "react";
import axios from "axios";
import Card from "./components/Card";
import Header from "./components/Header";
import Drawer from "./components/Drawer"

function App() {
    const [items, setItems] = React.useState([])
    const [cartItems, setCartItems] = React.useState([])
    const [searchValue, setSearchValue] = React.useState("")
    const [cartOpened, setCartOpened] = React.useState(false)

    React.useEffect(() => {
        axios.get('https://64a01998ed3c41bdd7a6ff00.mockapi.io/items').then(res => {
            setItems(res.data)
        })
        fetch('https://64a01998ed3c41bdd7a6ff00.mockapi.io/Cart').then(res => {
            setCartItems(res.data)
        })
    }, [])

    const onAddToCart = (obj)=>{
        axios.post('https://64a01998ed3c41bdd7a6ff00.mockapi.io/Cart', obj)
        setCartItems(prev => [...prev, obj])
    }
    const onRemoveToCart = (id) => {
        axios.delete(`https://64a01998ed3c41bdd7a6ff00.mockapi.io/Cart/${id}`)
        console.log(id)
        setCartItems((prev) => prev.filter((item) => item.id !== id))
    }
    const onChangeSearchInput = (event) =>{
        setSearchValue(event.target.value)
    }
  return (
    <div className="wrapper clear">
        {cartOpened ? <Drawer items={cartItems} onClickClose={()=> setCartOpened(false)} onRemove={onRemoveToCart}/> : null }
        <Header key={"Header"} onClickCart={ () => setCartOpened(true)}/>
      <section className="content p-40">
          <div className="d-flex justify-between align-center mb-40">
              <h1>{searchValue ? `Поиск по запросу "${searchValue}"` : "Все кроссовки"}</h1>
              <div className="search-block">
                  <img src="/img/search.svg" alt="search"/>
                  <input value={searchValue} onChange={onChangeSearchInput} placeholder="Поиск..." type="text"/>
              </div>
          </div>
          <div className="d-flex flex-wrap">
              {items.filter((item) =>(item.title.toLowerCase().includes(searchValue.toLowerCase()))).map((item, index) =>(<Card
                  key={index}
                  title={item.title}
                  price={item.price}
                  imageUrl={item.imageUrl}
                  onPlus={(obj) => onAddToCart(obj)}
                  onFavorite={()=>{console.log('Добавили в закладки')}}
              /> ))}
          </div>
      </section>
    </div>
  );
}
export default App;
