import Card from "./../../Card/index";
import styles from "./Home.module.scss"
import React from "react";
import axios from "axios";


const Home = ({items, searchValue, onAddToCart, onChangeSearchInput, onClickFavorite, cartItems, loading}) => {
    return (
        <section className={styles.content}>
            <div className="d-flex justify-between align-center mb-40">
                <h1>{searchValue ? `Поиск по запросу "${searchValue}"` : "Все кроссовки"}</h1>
                <div className={styles.searchBlock}>
                    <img src="/img/search.svg" alt="search"/>
                    <input value={searchValue} onChange={onChangeSearchInput} placeholder="Поиск...." type="text"/>
                </div>
            </div>
            <div className="d-flex flex-wrap">
                {items.filter((item) =>(item.title.toLowerCase().includes(searchValue.toLowerCase()))).map((item, index) =>(<Card
                    key={index}
                    onPlus={(obj) => onAddToCart(obj)}
                    added={cartItems.some(obj =>  Number(obj.id) === Number(item.id))}
                    onFavorite={(obj)  => onClickFavorite(obj)}
                    loading={loading}
                    {...item}
                /> ))}
            </div>
        </section>
    )
}
export default Home
